import React, { useState } from 'react'
import ExpenseItem from './ExpenseItem';
import './Expenses.css';
import Card from './Card';
import ExpensesFilter from './ExpensesFilter/ExpensesFilter'
const Expenses = ({ expenses }) => {
    const [currentYear, setCurrentYear] = useState('2021')

    const onSelectYear = (selectedYear) => {
        setCurrentYear(selectedYear)
    }

    const filteredExpenses = expenses.filter(expense => {
        return expense.date.getFullYear().toString() === currentYear;
    });

    let expensesContent = <p>You have no expenses for this year.</p>;
    if (filteredExpenses.length > 0) {
        expensesContent = filteredExpenses.map(expense => {
            return <ExpenseItem
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
                key={expense.id} />
        });
    }

    return (
        <Card className='expenses'>
            <ExpensesFilter selected={currentYear} onSelectYear={onSelectYear} />
            {expensesContent}
        </Card>
    )
}

export default Expenses
